<?phpnamespace PRS\Model;////////////////////////   // Підключення БД //  //////////////////////////////////////////////////////////////////////////////class DB{	//  --------------------------------------------------------------------------//  екземпляр класу	private static $instance;//  підключення БД	private $pdo;	//  --------------------------------------------------------------------------//	синглтон//  --------------------------------------------------------------------------	public static function Instance() 	{		if (self::$instance == null)			self::$instance = new DB();		return self::$instance;	}//  --------------------------------------------------------------------------//	Конструктор//  --------------------------------------------------------------------------	private function __construct()	{		$dsn = "mysql:host=" . MYSQL_SERVER . ";dbname=" . MYSQL_DB . ";charset=utf8";		$opt = array(			\PDO::ATTR_ERRMODE => \PDO::ERRMODE_EXCEPTION,			\PDO::ATTR_DEFAULT_FETCH_MODE => \PDO::FETCH_OBJ);		$this->pdo = new \PDO($dsn, MYSQL_USER, MYSQL_PASSWORD, $opt);		$this->pdo->exec('SET session wait_timeout=3600');	}	//  --------------------------------------------------------------------------//	Віддаємо ПДО//  --------------------------------------------------------------------------	public function GetConnect()	{		return $this->pdo;	}}	