<?phpnamespace PRS\Model;//////////////////////////////////   // Підключення БД для логів //  //////////////////////////////////////////////////////////////////////////////class DBL{	//  --------------------------------------------------------------------------//  екземпляр класу	private static $instance;//  підключення БД	private $pdo;	//  --------------------------------------------------------------------------//	синглтон//  --------------------------------------------------------------------------	public static function Instance() 	{		if (self::$instance == null)			self::$instance = new DBL();		return self::$instance;	}//  --------------------------------------------------------------------------//	Конструктор//  --------------------------------------------------------------------------	private function __construct()	{		$dsn = "mysql:host=" . MYSQL_SERVER_LOG . ";dbname=" . MYSQL_DB_LOG . ";charset=utf8";		$opt = array(			\PDO::ATTR_ERRMODE => \PDO::ERRMODE_EXCEPTION,			\PDO::ATTR_DEFAULT_FETCH_MODE => \PDO::FETCH_OBJ);		$this->pdo = new \PDO($dsn, MYSQL_USER_LOG, MYSQL_PASSWORD_LOG, $opt);		$this->pdo->exec('SET session wait_timeout=2000');	}	//  --------------------------------------------------------------------------//	Віддаємо ПДО//  --------------------------------------------------------------------------	public function GetConnect()	{		return $this->pdo;	}}	